mapper.select:
select 

from ico_mall_auctionstate
where auction_commodity_id = #{auctionCommodityId,jdbcType=VARCHAR}
  and auction_user_id = #{auctionUserId,jdbcType=VARCHAR}

mapper.delete:
delete from ico_mall_auctionstate
where auction_commodity_id = #{auctionCommodityId,jdbcType=VARCHAR}
  and auction_user_id = #{auctionUserId,jdbcType=VARCHAR}

mapper.insert:
insert into ico_mall_auctionstate (auction_commodity_id, auction_user_id, 
  auction_price, auction_due_date, auction_due_time
  )
values (#{auctionCommodityId,jdbcType=VARCHAR}, #{auctionUserId,jdbcType=VARCHAR}, 
  #{auctionPrice,jdbcType=DECIMAL}, #{auctionDueDate,jdbcType=CHAR}, #{auctionDueTime,jdbcType=CHAR}
  )

mapper.update:
update ico_mall_auctionstate


    auction_price = #{auctionPrice,jdbcType=DECIMAL},


    auction_due_date = #{auctionDueDate,jdbcType=CHAR},


    auction_due_time = #{auctionDueTime,jdbcType=CHAR},


where auction_commodity_id = #{auctionCommodityId,jdbcType=VARCHAR}
  and auction_user_id = #{auctionUserId,jdbcType=VARCHAR}

update ico_mall_auctionstate
set auction_price = #{auctionPrice,jdbcType=DECIMAL},
  auction_due_date = #{auctionDueDate,jdbcType=CHAR},
  auction_due_time = #{auctionDueTime,jdbcType=CHAR}
where auction_commodity_id = #{auctionCommodityId,jdbcType=VARCHAR}
  and auction_user_id = #{auctionUserId,jdbcType=VARCHAR}
